INSERT INTO `tbl_history` (`id`, `date`, `title`, `text`, `class`, `type`, `status`) VALUES
(1, '', ' ТзОВ «Агротехніка» Сьогодні', '<strong>Сьогодні&nbsp;ТзОВ &laquo;Агротехніка&raquo;&nbsp;- успішна компанія, що динамічно розвивається та вдосконалюється, постійно нарощує власні виробничі потужності, розширює асортимент своїх товарів.</strong><br />\r\n<br />\r\nСистемність та послідовність у веденні бізнесу, а також наслідування основних тенденцій світового ринку, дає можливість підприємству підтримувати статус надійного та стабільного партнера. Ефективність менеджменту, передові технології, професіональна команда, оптимальне використання людських, природних та фінансових ресурсів &ndash; ось, що дозволяє нам показувати високі результати та стрімко рухатись вгору.\r\n', '', 0, 1),
(3, '2014 рік', 'Елеватор та зерносушильний комплекс в с.Несвіч', '<p>Елеватор та зерносушильний комплекс в с.Несвіч - готується до запуску у 2014р.</p>\r\n', '', 1, 1),
(16, '2014 рік', 'Елеваптор в смт.Турійськ', '<p>Елеваптор в смт.Турійськ &ndash; запуск у 2014р.</p>\r\n', '', 0, 1),
(17, '2013 рік', 'Елеватор в смт.Локачі', '<p>Елеватор в смт.Локачі &ndash; запуск 2013 р.</p>\r\n', '', 1, 1),
(18, '2012 рік', 'Цех переробки олійний культур', '<p><strong>Цех переробки олійний культур &ndash; запуск 2012р.</strong></p>\r\n\r\n<p>На початку 2012 року на базі підприємства розпочав роботу&nbsp;<strong>цех по переробці олійних культур</strong>. З цього моменту виробництво олії та інших продуктів переробки олійних культур стало важливою складовою діяльності підприємства. Варто зазначити, що продукція олійного цеху використовується, як для забезпечення потреб власних комбікормових заводів, так і для реалізації на внутрішньому ринку та за кордоном.</p>\r\n', '', 0, 1),
(19, '2009 рік', 'Турійский комбікормовий завод', '<p><strong>Турійский комбікормовий завод &ndash; запуск виробництва 2009 р.</strong></p>\r\n\r\n<p>Одним із основних напрямків діяльності ТзОВ &laquo;Агротехніка&raquo; є виробництво та постачання повнораціонних комбікормів для сільськогосподарських тварин, птахів і риби та концентратів під&nbsp;<strong>торговою маркою &laquo;Агрокорм&raquo;</strong>&nbsp;.</p>\r\n\r\n<p>Два заводи з високотехнологічним обладнанням і повна автоматизація виробництва дозволяють виготовляти широкий асортимент комбікормів, легко змінюючи рецептуру, вигляд і спосіб пакування. Середньомісячна потужність заводів складає 12000 тон гранульованого комбікорму.</p>\r\n', '', 1, 1),
(20, '2002 рік', 'Локачинський комбікормовий завод', '<p>Локачинський комбікормовий завод &ndash; запуск виробництва 2.08.2002</p>\r\n', '', 0, 1);

