INSERT INTO `tbl_power` (`id`, `title`, `text`, `image`, `status`) VALUES
(1, 'Зерносховище (елеватор) смт. Локачі', '<p><strong>Адреса потужностей:</strong><br />\r\nвул. Шевченка 14, смт.Локачі, Локачинський район, Волинська область, 45300.</p>\r\n\r\n<p><strong>Сертифікат</strong> відповідності послуг із зберігання зерна та продуктів його переробки виданий Державною інспекцією сільського господарства України від 03 жовтня 2013 року №6398<br />\r\nВсі склади обладнані активною вентиляцією, що забезпечує довгострокове зберігання. ВТЛ(виробнича технологічна лабораторія) акредитована та забезпечує відповідність якісних показників під час прийому, доробки( очистки та сушіння), зберігання. Приймання зерна здійснюється у відповідності до&nbsp; &laquo;Інструкції про ведення обліку й оформлення операцій із зерном і продуктами його переробки на хлібоприймальних та зернопереробних підприємствах&raquo;, затвердженої наказом Міністерства аграрної політики України № 661 від 13.10 2008р. та діючими державними стандартами для кожного конкретного виду зерна.Відвантаження зерна здійснюється у відповідності до вимог Інструкції та Положення про обіг складських документів на зерно, затвердженим наказом міністерства аграрної політики України&nbsp; від 27.06.2003 № 198.<br />\r\nЗерносклад обладнаний 2 сучасними зерносушильними комплексами. <strong>МС1195 ВЕМ -нижня сушка</strong><strong>, </strong><strong>VGT6413- верхня сушка.</strong></p>\r\n\r\n<p><strong>ТЕХНИЧНА ХАРАКТЕРИСТИКА</strong><br />\r\nзернового складу смт.Локачі.</p>\r\n\r\n<ol>\r\n	<li>&nbsp;Тип зернового складу: силосний</li>\r\n	<li>&nbsp;Потужність з зберігання 10000тон: силос№1-5000тон, силос №2-5000тон.</li>\r\n	<li>&nbsp;Добова пропускна потужність: приймання з автомобіля 350тон., відвантаження на автомобіль 250тон.</li>\r\n	<li>&nbsp;Потужності по доробці (очистка, сушка):</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style="margin-left: 40px;">пшениця, ячмінь - 1200 тон в добу</li>\r\n	<li style="margin-left: 40px;">кукурудза - 550 тон в добу</li>\r\n	<li style="margin-left: 40px;">ріпак - 300 тон в добу</li>\r\n	<li style="margin-left: 40px;">соя - 800 тон в добу.</li>\r\n</ul>\r\n\r\n<p>5. Застосовані енергоносії - газ, електроенергія.</p>\r\n', '539ac20d5ad69.jpg', 1),
(2, 'Зерносховище Турійськ', '<p><strong>Місцезнаходження</strong>: вул. Паралельна 33, смт.Турійськ, Турійського район, Волинська область.</p>\r\n\r\n<p><strong>Сертифікат</strong> відповідності послуг із зберігання зерна та продуктів його переробки виданий Державною інспекцією сільського господарства України (сертифікація проводиться в даний час)<br />\r\nВсі склади обладнані активною вентиляцією, що забезпечує довгострокове зберігання.<br />\r\nВТЛ(виробнича технологічна лабораторія) акредитована та забезпечує відповідність якісних показників під час прийому, доробки( очистки та сушіння), зберігання.<br />\r\nПриймання зерна здійснюється у відповідності до&nbsp; &laquo;Інструкції про ведення обліку й оформлення операцій із зерном і продуктами його переробки на хлібоприймальних та зернопереробних підприємствах&raquo;, затвердженої наказом Міністерства аграрної політики України № 661 від 13.10 2008р. та діючими державними стандартами для кожного конкретного виду зерна<br />\r\nВідвантаження зерна здійснюється у відповідності до вимог Інструкції та Положення про обіг складських документів на зерно, затвердженим наказом міністерства аграрної політики України&nbsp; від 27.06.2003 № 198.</p>\r\n\r\n<p><strong>ТЕХНИЧНА ХАРАКТЕРИСТИКА</strong><br />\r\nзернового складу смт.Турійськ.</p>\r\n\r\n<ol>\r\n	<li>&nbsp;Тип зернового складу: силосний, напільний .</li>\r\n	<li>&nbsp;Потужність з зберігання силосний 3000тон: силос№1-1500тон, силос №2-1500тон, напільний 7200 тон: склад№3 -1200тон, склад №4- 2800тон, склад№5 -3200 тон.</li>\r\n	<li>&nbsp;Добова пропускна потужність: приймання з автомобіля 350тон., відвантаження на автомобіль 250тон., приймання з залізничного транспорту 250тон., відвантаження на залізничний транспорт до 400тон.</li>\r\n	<li>Потужності по доробці (очистка, сушка):</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style="margin-left: 40px;">пшениця, ячмінь - 200 тон в добу</li>\r\n	<li style="margin-left: 40px;">кукурудза - 150 тон в добу</li>\r\n	<li style="margin-left: 40px;">ріпак - 120 тон в добу</li>\r\n	<li style="margin-left: 40px;">соя - 120 тон в добу.</li>\r\n</ul>\r\n\r\n<p><strong>&nbsp; &nbsp; &nbsp; 5. </strong>Застосовані енергоносії - газ, електроенергія.</p>\r\n', '53b2e1e98449c.jpg', 1),
(3, 'Зерносховище с.Чаруків', '<p><strong>Місцезнаходження зерносховищ</strong>:<br />\r\nвул. Завокзальна 156, с.Чаруків, Луцький район, Волинська область, .</p>\r\n\r\n<p><strong>Сертифікат</strong> відповідності послуг із зберігання зерна та продуктів його переробки виданий Державною інспекцією сільського господарства України( після добудови першої черги буде сертифікація)<br />\r\nВсі склади обладнані активною вентиляцією, що забезпечує довгострокове зберігання.<br />\r\nВТЛ(виробнича технологічна лабораторія) акредитована та забезпечує відповідність якісних показників під час прийому, доробки( очистки та сушіння), зберігання.<br />\r\nПриймання зерна здійснюється у відповідності до&nbsp; &laquo;Інструкції про ведення обліку й оформлення операцій із зерном і продуктами його переробки на хлібоприймальних та зернопереробних підприємствах&raquo;, затвердженої наказом Міністерства аграрної політики України № 661 від 13.10 2008р. та діючими державними стандартами для кожного конкретного виду зерна<br />\r\nВідвантаження зерна здійснюється у відповідності до вимог Інструкції та Положення про обіг складських документів на зерно, затвердженим наказом міністерства аграрної політики України&nbsp; від 27.06.2003 № 198<br />\r\nЗерносклад обладнаний&nbsp; сучасним зерносушильними комплексом на альтернативному паливі(салома) .</p>\r\n\r\n<p><strong>ТЕХНИЧНА ХАРАКТЕРИСТИКА</strong><br />\r\nзернового складу с.Чаруків.</p>\r\n\r\n<ol>\r\n	<li>&nbsp;Тип зернового складу: силосний</li>\r\n	<li>&nbsp;Потужність з зберігання 15000тон: силос№1-5000тон(збудований), силос №2-5000тон.(збудований),силос№3-5000тон(будується),</li>\r\n	<li>&nbsp;Добова пропускна потужність: приймання з автомобіля 350тон., відвантаження на автомобіль 250тон., завантаження на залізничний транспорт до 600тон.</li>\r\n	<li>Потужності по доробці (очистка, сушка):</li>\r\n</ol>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>пшениця, ячмінь - 600 тон в добу</li>\r\n	<li>кукурудза - 200 тон в добу</li>\r\n	<li>ріпак - 300 тон в добу</li>\r\n	<li>соя - 300 тон в добу</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; 5. Застосовані енергоносії - солома, електроенергія.</p>\r\n', '53b2e26cb0ab2.jpg', 1),
